import React from "react";

const PokemonItem = ({pokemon}) => {
  const imgSrc = pokemon.isCatch ? 'https://raw.gitubusercontent.com/PokeAPI/sprites/master/sprites/items/poke-ball.png' : pokemon.image 
  return (
    <li className="pokemon-item">
      <img
        alt={pokemon.name}
        src={pokemon.image}
      />
      {pokemon.name}
    </li>
  );
};

export default PokemonItem;
